<?phpclass Submenu extends SEO{	public $properties = array(			"id"=>null,			"category"=>null,			"parent_id"=>0,			"title"=>'',			"description"=>'',			"image"=>null,			"lang"=>'bg',			"order_no"=>'',			"external"=>'',			"featured"=>0		);			function __construct()	{		parent::__construct();		$this->tableName = "submenus";	}		function loadItem($url, $category)	{		$url = addslashes($url);		$result = $this->select("id", "#table", "deleted = 0 AND category = $category AND url = '$url'");		while ($row = $result->fetch_object())		{			$this->load($row->id);		}	}}?>