<form action="index.php" method="post" id="form"><input type="hidden" name="content" value="Categories" /><input type="hidden" name="action" value="save" /><input type="hidden" name="id" value="<?php echo $category->id; ?>"/><h2><?php echo $title; ?></h2><table class="full">	<tr class="line">		<th><?php echo $lang['Category']; ?></th>		<td><input class="textbox required" type="text" name="title" value="<?php echo $category->title; ?>"/></td>	</tr>	<tr class="line">		<th>Раздел</th>		<td>			<select name="cat_type">				<option value="1" <?php if ($category->cat_type == 1) echo 'selected="selected"' ?>>Статии</option>				<option value="2" <?php if ($category->cat_type == 2) echo 'selected="selected"' ?>>Продукти</option>			</select>		</td>	</tr></table><div style="clear: both; padding: 5px 0;">	<a class="submit"><?php echo $lang['Save']; ?></a>	<a class="cancel"><?php echo $lang['Cancel']; ?></a></div><script type="text/javascript">prepareUI();jQuery(".cancel").click(function(){	jQuery(".right").html('');});jQuery(".submit").click(function(){	if (validate('#form'))	{		jQuery.post("index.php",		{			content: "Categories",			action: "checkUrl",			id: '<?php echo $category->id; ?>',			title: jQuery('input[name="title"]').val()		},		function(data)		{			if (data == "OK")			{				jQuery("#form").submit();			}			else			{				jQuery('input[name="title"]').addClass('invalid1');				jQuery(".alertDlg").html('<?php echo $lang['Choose another title']; ?>');				jQuery(".alertDlg").dialog("open");			}		});	}	else	{		jQuery(".alertDlg").html('<?php echo $lang['Fill in red']; ?>');		jQuery(".alertDlg").dialog("open");	}});</script></form>